import React, {Component} from 'react';
import Aux from '../../../hoc/Aux';
import Button from '../../UI/Button/Button';

class OrderSummary extends Component {
    componentWillUpdate () {
         console.log('[ORderSummary] WillUpdate');
    }
    render (){
         const ingredientSummary = Object.keys(this.ingredients).map(igKey => {
        return (
        <li key={igKey}>
            <span style={{textTransform: 'capitalize '}}>{igKey}</span>: {this.ingredients[igKey]}
        </li>
        );
    });
        return(
            <Aux>
           <h3>Your Order</h3>
           <p>A delicious burger with the following ingredients:</p>
           <ul>
               {ingredientSummary}
           </ul>
           <p><strong>Total Price: {this.price.toFixed(2)}</strong> </p>
           <p>Continue to checkout?</p>
           <Button btnType="Danger" clicked={this.purchaseCancelled}>CANCEL</Button>
           <Button btnType="Success" clicked={this.purchaseContinued}>CONTINUE</Button>
       </Aux>
        )

    }

};


 export default OrderSummary;
